import React, { Component } from 'react'
import axios from 'axios';
const URL_PATH = 'http://localhost:8081/power-plants';

export default class PowerPlantList extends Component {
    constructor(props){
        super(props)
        //this.handleClick = this.handleClick.bind(this);
       // this.stopbutton = this.stopbutton.bind(this);
      // this.loadMore = this.loadMore.bind(this);
        this.state = {
          data: [],
          filter:"",
          alert:false,
          isActive:false,
          page:1,
          query: '',
        }

      }

      componentWillMount(){
        this._refreshMovies(); 

     }
     _refreshMovies(query) {
        const {   data } = this.state;


        axios.get(URL_PATH).then(response => {
         console.log(response); 
          this.setState({
            data: response.data,

          });

        });
      }

    render() {
        const getData = this.state.data;
        return (

            <div className="row"> 
            <table>
        <thead>
          <tr>
              <th>Name</th>
              <th>Item Name</th>
              <th>Item Price</th>
          </tr>
        </thead>

        <tbody>
          <tr>
            <td>Alvin</td>
            <td>Eclair</td>
            <td>$0.87</td>
          </tr>
          <tr>
            <td>Alan</td>
            <td>Jellybean</td>
            <td>$3.76</td>
          </tr>
          <tr>
            <td>Jonathan</td>
            <td>Lollipop</td>
            <td>$7.00</td>
          </tr>
        </tbody>
      </table>   
            {getData.map((row, i)=>(
                <div className="col s12 m6 l6 card horizontal box" key={i}>

                      <div className="card-stacked">  
                          <h5>{row.name}</h5>


                  </div>
                </div>
            ))} 
            </div>
        )
    }
}
