import React, { Component } from 'react'
import axios from 'axios';
import Rating from '@material-ui/lab/Rating';
import Button from '@material-ui/core/Button';
import ReadMoreReact from 'read-more-react';
const URL_PATH = 'https://api.themoviedb.org/3/discover/movie?api_key=765f50fa6e7727408e9fb5e569a800cd&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1';
const IMG_PATH = 'https://image.tmdb.org/t/p/w185_and_h278_bestv2/';
// view more content
const minimumLength = 50
const idealLength = 100
const maxLength = 200
export default class MovieListing extends Component {
    constructor(props){
        super(props)
        //this.handleClick = this.handleClick.bind(this);
       // this.stopbutton = this.stopbutton.bind(this);
       this.loadMore = this.loadMore.bind(this);
        this.state = {
          movies: [],
          filter:"",
          alert:false,
          isActive:false,
          page:1,
          query: '',
        }

      }

       componentWillMount(){
         this._refreshMovies(); 
         this.scrollListener = window.addEventListener("scroll", e => {
         this.handleScroll(e);
          });  
      }

      handleOnInputChange = event => {
        const query = event.target.value;
        this.setState( { query, loading: true, message: '' }, () => {
          this._refreshMovies(query );
        } );
        };


      _refreshMovies(query) {
        const {   movies } = this.state;


        const URL_PATH = 'https://api.themoviedb.org/3/discover/movie?api_key=765f50fa6e7727408e9fb5e569a800cd&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page='+this.state.page;
        axios.get(URL_PATH).then(response => {
         console.log(URL_PATH); 
          this.setState({
            //movies: response.data.results,
            movies: [...movies, ...response.data.results],
          });

        });
      }




      loadMore = () => {
        this.setState(
          prevState => ({
            page: prevState.page + 1,
            scrolling: true
          }),
          this._refreshMovies
        );
        //console.log(this.state.page)
      };

      handleScroll = () => { 
        var lastLi = document.querySelector("div.row > div:last-child");
        var lastLiOffset = lastLi.offsetTop + lastLi.clientHeight;
        var pageOffset = window.pageYOffset + window.innerHeight;
      if (pageOffset > lastLiOffset) {
             this.loadMore();
        }
      };

      filterChamp(e){
        this.setState({filter: e.target.value})
      }



    render() {
        //console.log(this.state.movies);
        const getData = this.state.movies;
        const isButtonClick = this.state.isActive;

        if(this.state.filter){
            if(this.state.filter == 'desc'){
                getData.sort((a, b) => b.vote_average - a.vote_average);
            } else if(this.state.filter == 'asc'){
                getData.sort((a, b) => a.vote_average - b.vote_average);    
            } else if(this.state.filter == 'late'){
                getData.sort((a, b) => b.id - a.id);    
            }
        } 


        return (
            <div className="container">
                   <h3>Movie List</h3>
              <div>
                {/* {this.state.alert ? <SuccessAlert alert={this.state.alert} /> : null} */}

              </div>
              <div className="row">
                  <div className="col m4 s4 center-align">
                  {/* <input
					type="text"
					name="query"
					value={ this.state.query }
					id="search-input"
					placeholder="Search..."
					onChange={this.handleOnInputChange}
				/> */}
                  </div>
                  <div className="col m4 s4 center-align">
                      <label>Sort By</label>
                      <select onChange={this.filterChamp.bind(this)} value={this.state.filterChamp} className="browser-default">
                          <option value="">Select</option>
                          <option value="late">Latest</option>
                          <option value="desc">Rating Descending</option>
                          <option value="asc">Rating Ascending</option>
                      </select>
                  </div>

              </div> 
                  <div className="row">    
                {getData.map((row, i)=>(
                    <div className="col s12 m6 l6 card horizontal box" key={i}>
                          <div className="card-image">  
                              <img src={IMG_PATH+row.poster_path} alt={row.title}/>
                          </div> 
                          <div className="card-stacked">  
                              <h5>{row.title}</h5>
                              <ReadMoreReact text={row.overview}
                                  min={minimumLength}
                                  ideal={idealLength}
                                  max={maxLength}
                                  readMoreText="Read more..."/>
                              <div className="card-action">           
                              <Rating
                                  name= { "movie-id-" + row.id }  
                                  value={row.vote_average/2}
                                  max={5}
                                  onChange={this.handleNameInput}
                                  />
                          </div>
                      </div>
                    </div>
                ))} 
                <div className="c12">
                <Button
                onClick={e => {
                this.loadMore();
                }}
                >
                Load More
                </Button>
                </div>
              </div>

            </div>
        )
    }
}
