import React, { Component,useEffect } from 'react'
import axios from 'axios';
 const URL_PATH = 'https://reactdemoapi.herokuapp.com/power-plants';
//const URL_PATH = 'http://localhost:8081/power-plants';


export default class PowerPlantList extends Component {

    constructor(props){
        super(props)

        this.state = {
          data: [],
          filter:"",
          totalPages: '',
          nextPage: '',
          view:'table',
          selectedRow:''
        }
        this._loadData = this._loadData.bind(this);

      }

      componentWillMount(){

        setInterval(()=> this._loadData(), 10000)

     }
     filterChamp(e){
        this.setState({filter: e.target.value})
      }
     _loadData() {
        const {   data } = this.state;
        axios.get(URL_PATH).then(response => {
         console.log(response); 
          this.setState({
            data: response.data,

          });

        });
      }





    render() {



        const getData = this.state.data;

        if(this.state.filter){
            if(this.state.filter == 'WIND_ONSHORE'){
              const {   data } = this.state;
                const results = this.state.data.filter( function (data) {
                   return data.type === 'WIND_ONSHORE'
                 });
                 this.setState({
                  //movies: response.data.results,
                  data: [...data, ...results],
                });
            } 
        } 


        return (

          <div className="container"> 
            <h3>Power Plants</h3>
            <div className="row">
                  <div className="col m4 s4 center-align">
                      <label>Sort By</label>
                      <select onChange={this.filterChamp.bind(this)} value={this.state.filterChamp} className="browser-default">
                          <option value="">Select</option>
                          <option value="WIND_ONSHORE">On Shore</option>
                          <option value="WIND_OFFSHORE">Off Shore</option>
                          <option value="HYDRO">Hydro</option>
                          <option value="BIOMASS">BIOMASS</option>
                      </select>
                  </div>
                  <div className="col m4 s4 center-align">
                      Map View
                   </div>
              </div> 

         <table>
        <thead>
          <tr>
              <th>S.No</th>
              <th>Bundesnetza gentur Number</th>
              <th>Company</th>
              <th>Type</th>
              <th>State</th>
              <th>Production Value (kW)</th>
              <th>Agg.Production (kW)</th>
          </tr>
        </thead>

        <tbody>
        {this.state.data.map((row, i)=>(
          <tr key={i} className={this.state.selectedRow === i%2 ? "tableSelected" : "" }
          > 
            <td>{i+1}</td>
            <td>{row.bundesnetzagentur_number}</td>
            <td>{row.comapny_name}</td>
            <td>{row.type}</td>
            <td>{row.state}</td>
            <td>{row.production_value_kw}</td>
            <td>{row.aggregated_production_kw}</td>
          </tr>
          ))} 

        </tbody>
      </table>   

            </div>
        )
    }
}
