import React, { Component } from 'react'
import axios from 'axios';
import TableView from './TableView';
import Button from './Button';
import MapView from './MapView';
 const URL_PATH = 'https://reactdemoapi.herokuapp.com/power-plants';
//const URL_PATH = 'http://localhost:8081/power-plants';



export default class PowerPlantList extends Component {

    constructor(props){
        super(props)

        this.state = {
          data: [],
          filter:"",
          view:'table'
        }
        this._loadData = this._loadData.bind(this);

      }

      componentWillMount(){

        setInterval(()=> this._loadData(), 10000)

     }
     filterChamp(e){
        this.setState({filter: e.target.value})
      }
     _loadData() {
        const {   data } = this.state;
        axios.get(URL_PATH).then(response => {
         console.log(response); 
          this.setState({
            data: response.data,

          });

        });
      }


    render() {
      // filer
      let getData;
      if(this.state.filter !='') {
        getData = this.state.data.filter(
        (dt) =>{
          console.log(dt.type)
          return dt.type === this.state.filter;
        }
      );
      } else {
         getData =  this.state.data;
      }




        return (

          <div className="container"> 
            <h3>Power Plants</h3>
            <div className="row">
                  <div className="col m4 s4 center-align">
                      <label>Sort By</label>
                      <select onChange={this.filterChamp.bind(this)} value={this.state.filterChamp} className="browser-default">
                          <option value="">Select</option>
                          <option value="WIND_ONSHORE">On Shore</option>
                          <option value="WIND_OFFSHORE">Off Shore</option>
                          <option value="HYDRO">Hydro</option>
                          <option value="BIOMASS">BIOMASS</option>
                      </select>
                  </div>
                  <div className="col m4 s4 center-align">
                    <Button/>
                   </div>
              </div> 
          {/* <MapView data={getData}/>  */}
          <TableView data={getData}  /> 
            </div>
        )
    }
}
